mapper.select:
select
dep_id, name, address, status
from rd_ssm_project.department


        and dep_id = #{depId}


        and name = #{name}


        and address = #{address}


        and status = #{status}

mapper.update:
update rd_ssm_project.department
set status = 1
where dep_id = #{depId}

update rd_ssm_project.department
set status = 2
where dep_id = #{depId}
